import {nanoid} from 'nanoid';
const initialstate={
    todos:[],
    username:'',
    isAuthenticated:false,
    errorMessage:'',
}
const todoReducer=(state=initialstate,action)=>{
    if(action.type==='add-todo')
    {
        let updatedTodos=[...state.todos,{
            text:action.enteredTask,
            id:nanoid()
        }]
