import React,{useState} from 'react';
import './TaskList.css';
const TaskList=(props)=>{
    console.log(props.items);
    const [tasks,handleTasks]=usestate(props.items);
    const deleteHandler=(key)=>{
        const newTasks=tasks.filter(task=>{return task.id!==key});
        handleTasks(newTasks);
    }
    return(
        <ul className='task-list'>
            {
                tasks.map(task=>{
                    // console.log(task);
                    //console.log(task.id);   
                    return <li className='task'>{task.text}<i class="fa-sharp fa-solid fa-trash" id='delete' onClick={deleteHandler(task.id)}></i></li>
                })
            }
        </ul>
    );
}
export default TaskList;
