import {legacy_createStore} from 'redux';
import {nanoid} from 'nanoid';
const initialstate={
    todos:[],
    username:'',
    isAuthenticated:false,
    errorMessage:''
}
const todoReducer=(state=initialstate,action)=>{
    if(action.type==='add-todo')
    {
        let updatedTodos=[...state.todos,{
            text:action.data,
            id:nanoid()
        }]
        return{
            ...state,
            todos:updatedTodos
        };
    }
    else if(action.type==='remove-todo')
    {
        let updatedTodos=state.todos.filter(item=>{
            return item.id!==action.key;
        })
        return{
            ...state,
            todos:updatedTodos
        }
    }
    else if(action.type==='logout')
    {
        return {
            isAuthenticated:false,
            todos:[],
            username:'',
            errorMessage:''
        }
    }
    else if(action.type==='login')
    {
        return {
            isAuthenticated:true,
            todos:[],
            username:'',
            errorMessage:''
        }
    }
    else if(action.type==='signup')
    {
        action.user['todos'] = []
        localStorage.setItem(action.user.email,JSON.saction.user)
        return {
            isAuthenticated:true,
            todos:[],
            username:action.user.email,
            errorMessage:''
        }
    }
    return state;
}
const store=legacy_createStore(todoReducer);

export default store;
